// Get extracted data from Information Extractor
const extracted = $input.item.json.output || $input.item.json;

// Check if ANY contact field has a real value
const hasAnyContact = Boolean(
  (extracted.phone && extracted.phone.trim() !== '') ||
  (extracted.email && extracted.email.trim() !== '') ||
  (extracted.telegram && extracted.telegram.trim() !== '') ||
  (extracted.instagram && extracted.instagram.trim() !== '') ||
  (extracted.other && typeof extracted.other === 'string' && extracted.other.trim() !== '')
);

// Build contacts object (only non-empty values)
const contacts = {};
if (extracted.phone && extracted.phone.trim() !== '') contacts.phone = extracted.phone;
if (extracted.email && extracted.email.trim() !== '') contacts.email = extracted.email;
if (extracted.telegram && extracted.telegram.trim() !== '') contacts.telegram = extracted.telegram;
if (extracted.instagram && extracted.instagram.trim() !== '') contacts.instagram = extracted.instagram;
if (extracted.other && typeof extracted.other === 'string' && extracted.other.trim() !== '') contacts.other = extracted.other;

// Get original webhook data - use the node name to access it
const webhookNode = $('Trigger').first().json;  // Change 'Trigger' to your actual webhook node name
const originalMessage = webhookNode.body?.last_message || '';
const customerName = webhookNode.body?.chat_name || 'Unknown';

return {
  json: {
    hasContact: hasAnyContact,
    contacts: contacts,
    originalMessage: originalMessage,
    customerName: customerName,
    timestamp: new Date().toLocaleString('uk-UA', { timeZone: 'Europe/Kiev' }),
    webhookData: webhookNode
  }
};

const maxLength = 300;

// Get message
const messageText = ($input.item.json.body?.last_message || 
                     $input.item.json.last_message || 
                     $input.item.json.text || '').trim();

// Check if empty
if (!messageText || messageText.length === 0) {
  return {
    json: {
      blocked: true,
      reason: 'empty'
    }
  };
}

// Check if too long
if (messageText.length > maxLength) {
  return {
    json: {
      blocked: true,
      reason: 'too_long'
    }
  };
}

// ONLY USE CONTACT_ID (no fallbacks)
const sessionId = $input.item.json.body.contact_id;

// Valid message
return {
  json: {
    blocked: false,
    message: messageText,
    sessionId: sessionId
  }
};

// Get the message text from the body
const message = $input.item.json.body.message.toLowerCase().trim();

// Common greetings in Ukrainian and English
const greetings = [
  '–ø—Ä–∏–≤—ñ—Ç', 
  '–ø—Ä–∏–≤–µ—Ç',
  '–¥–æ–±—Ä–æ–≥–æ –¥–Ω—è', 
  '–¥–æ–±—Ä–∏–π –¥–µ–Ω—å',
  '–¥–æ–±—Ä–∏–π –≤–µ—á—ñ—Ä',
  '–¥–æ–±—Ä–æ–≥–æ –≤–µ—á–æ—Ä–∞',
  '–¥–æ–±—Ä–µ —Ä–∞–Ω–æ–∫',
  '–¥–æ–±—Ä–æ–≥–æ —Ä–∞–Ω–∫—É',
  '–∑–¥—Ä–∞—Å—Ç—É–π—Ç–µ', 
  '–∑–¥—Ä–∞—Å—Ç–µ',
  '–≤—ñ—Ç–∞—é',
  '–≤—ñ—Ç–∞–Ω–Ω—è',
  '–∑–¥—Ä–∞–≤—Å—Ç–≤—É–π—Ç–µ',
  '–∑–¥—Ä–∞–≤—Å—Ç–≤—É–π',
  '–¥–æ–±—Ä–æ—ó –Ω–æ—á—ñ',
  '–Ω–∞ –¥–æ–±—Ä–∞–Ω—ñ—á',
  '–¥—Ä–∞—Ç—É—Ç–∏',
  '–¥—Ä–∞—Å—Ç—É–π',
  '–π–æ',
  '—Ö–∞–π',
  '—Ö–µ–π',
  '—Ö–µ–ª–æ—É',
  '—Ö–µ–ª–ª–æ—É',
  'hey', 
  'hi', 
  'hello',
  'good morning',
  'good day',
  'good evening',
  'yo',
  'sup',
  '—Å–∞–ª–∞–º',
  '—à–∞–ª–æ–º',
  '–∞–ª–ª–æ',
  '–∫—É'
];

// Simple questions that don't need AI
const simpleQuestions = [
  '—è–∫ —Å–ø—Ä–∞–≤–∏',
  '—è–∫ —Å–∞–º',
  '—è–∫ —Ç–∏',
  '—â–æ –Ω–æ–≤–æ–≥–æ',
  '—à–æ –Ω–æ–≤–æ–≥–æ',
  '—â–æ —Ä–æ–±–∏—à',
  '—à–æ —Ä–æ–±–∏—à',
  'how are you',
  'whats up',
  'what\'s up'
];

// Check if message is just a greeting (exact match or starts with greeting)
const isGreeting = greetings.some(greeting => {
  return message === greeting || 
         message === greeting + '!' || 
         message === greeting + '.' ||
         message === greeting + '?' ||
         message === greeting + '!!' ||
         message === greeting + '??' ||
         message.startsWith(greeting + ' ') || 
         message.startsWith(greeting + ',');
});

// Check if it's a simple casual question
const isSimpleQuestion = simpleQuestions.some(question => {
  return message === question || 
         message === question + '?' ||
         message === question + '!' ||
         message.startsWith(question + ' ') ||
         question.startsWith(question + ',');
});

// Return the data with greeting flag
return {
  json: {
    ...$input.item.json,
    isGreeting: isGreeting || isSimpleQuestion
  }
};

// Get user message
const userMessage = ($input.item.json.body?.message || $input.item.json.message?.text || '').toLowerCase();

// Check if user is choosing phone consultation
const userWantsPhone = userMessage.includes('—Ç–µ–ª–µ—Ñ–æ–Ω') && 
                       (userMessage.includes('–∫–æ–Ω—Å—É–ª—å—Ç–∞—Ü') || 
                        userMessage.includes('–ø–æ —Ç–µ–ª–µ—Ñ–æ–Ω') ||
                        userMessage.includes('–ø–æ–¥–∑–≤–æ–Ω') ||
                        userMessage.includes('–∑–∞—Ç–µ–ª–µ—Ñ–æ–Ω'));

// Check if user is choosing to continue in chat
const userWantsChat = (userMessage.includes('—á–∞—Ç') || 
                       userMessage.includes('—Ç—É—Ç') ||
                       userMessage.includes('–ø—Ä–æ–¥–æ–≤–∂')) &&
                      !userWantsPhone;

// Check if user is providing a phone number (simple detection)
const hasPhoneNumber = /\+?3?8?0?\d{9}/.test(userMessage) || 
                       /\d{3}[-\s]?\d{3}[-\s]?\d{2}[-\s]?\d{2}/.test(userMessage);

return {
  json: {
    ...$input.item.json,
    userWantsPhone: userWantsPhone,
    userWantsChat: userWantsChat,
    hasPhoneNumber: hasPhoneNumber
  }
};

// Format available and taken slots for AI
const inputData = $input.all()[0].json;

// The data is an array, so we access the first element
const slotsData = Array.isArray(inputData) ? inputData[0] : inputData;

// Access the date_slots correctly
const dateSlots = slotsData.slots[0].date_slots;

// Process ALL dates (including those without available slots)
const allDates = dateSlots
  .filter(dayData => dayData.max_work_time > 0) // Only show working days
  .map(dayData => {
    const date = dayData.date;

    // Get all possible time slots from min to max work time
    const minTime = dayData.min_work_time;
    const maxTime = dayData.max_work_time;
    const duration = 3600; // 1 hour in seconds

    // Create array of all possible slots
    const allPossibleSlots = [];
    for (let time = minTime; time < maxTime; time += duration) {
      allPossibleSlots.push(time);
    }

    // Check which slots are available
    const availableSlotTimes = dayData.slots.map(slot => slot.time.start_time);

    // Create formatted time slots with status
    const formattedSlots = allPossibleSlots.map(startTime => {
      const startHour = Math.floor(startTime / 3600);
      const startMin = Math.floor((startTime % 3600) / 60);

      const timeStr = `${String(startHour).padStart(2, '0')}:${String(startMin).padStart(2, '0')}`;
      const status = availableSlotTimes.includes(startTime) ? '‚úì' : '‚úó';

      return `${timeStr}${status}`;
    });

    // Format date to DD.MM.YYYY
    const [year, month, dayNum] = date.split('-');
    const formattedDate = `${dayNum}.${month}.${year}`;

    return `${formattedDate}: ${formattedSlots.join(' ')}`;
  }); // Added semicolon here

// Get the original trigger data from further back in the workflow
const triggerData = $('Trigger').item.json;

return {
  json: {
    ...triggerData,  // Pass through all original data
    schedule: allDates.join('\n')  // Add the schedule
  }
};

const zones = $('Zones').all();
const tools = $('GetTools').all();
const rules = $('GetRules').all();
const types = $('GetShootTypes').all();

return [{
  json: {
    zonesText: zones.map(z => `${z.json['Zone Name']}: ${z.json['Description']}`).join('\n'),
    toolsText: tools.map(t => `${t.json['Item']}: ${t.json['Details']}`).join('\n'),
    rulesText: rules.map(r => `${r.json['Rule']}: ${r.json['Details']}`).join('\n'),
    typesText: types.map(t => t.json['Type']).join(', ')
  }
}];
